
public class Increment {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//Both pre and post increments will be used in loops. Preincrement will increment the variable value before it is used and vice-versa
		
		int i=0;
		System.out.println(++i);
		//int j=0;
		System.out.println(i++);
		System.out.println(i++);
		System.out.println(++i);
		}

}
